import { useEffect, useState, useCallback } from "react";
import Pin from "pin.svg";

export default ({ globalstate, updateGlobal }) => {
	const { index, markers, news } = globalstate;

	const active = index >= 0 ? markers[index] : false;

	const [nav, _nav] = useState("acts");

	const navigation = useCallback(key => {
		_nav(key);
	}, []);

	useEffect(() => {
		if (index >= 0) {
			navigation("local");
		}
	}, [index]);

	const SingleItem = useCallback(
		({ a, ax, news = false, updateGlobal }) => {
			return a ? (
				<div key={ax} className="author">
					<h3
						className={
							"title" + (nav === "acts" ? " clickable" : "")
						}
						onClick={() => {
							nav === "acts" && updateGlobal("index", ax);
						}}>
						{news ? a.title : `Пользователь #${a.id}`}
					</h3>

					<div className="desc">{a.content}</div>

					{a.source && a.source.length && (
						<div className="source">
							{a.source.map((s, sx) => (
								<div key={sx} className="src">
									{s.indexOf("youtube") >= 0 &&
									s.indexOf("iframe") >= 0 ? (
										<div
											dangerouslySetInnerHTML={{
												__html: s
											}}
										/>
									) : (
										<a href={s} target="_blank">
											{s}
										</a>
									)}
								</div>
							))}
						</div>
					)}
				</div>
			) : (
				"Нажмите не метку"
			);
		},
		[nav]
	);

	return (
		<div className={"block activityArea " + nav}>
			<nav>
				<ol>
					{[
						{
							id: "local",
							title: <Pin />
						},
						{ id: "acts", title: "Активность" },
						{ id: "news", title: "Новости" }
					].map((m, mx) => {
						return (
							<li
								key={mx}
								className={
									"navi " + (m.id === nav ? "active" : "")
								}
								onClick={() => {
									navigation(m.id);
								}}>
								{m.title}
							</li>
						);
					})}
				</ol>
			</nav>

			<div className={"activity"}>
				<div className="bb b1">
					{markers.map((a, ax) => {
						return (
							<SingleItem
								nav={nav}
								key={ax}
								a={a}
								ax={ax}
								updateGlobal={updateGlobal}
							/>
						);
					})}
				</div>

				<div className="bb b2">
					<SingleItem a={active} ax={0} updateGlobal={updateGlobal} />
				</div>

				<div className="bb b3">
					{news.map((a, ax) => {
						return (
							<SingleItem
								key={ax}
								news={true}
								a={a}
								ax={ax}
								updateGlobal={updateGlobal}
							/>
						);
					})}
				</div>
			</div>
		</div>
	);
};
