import { useEffect, useState } from "react";

export default ({ globalstate, updateGlobal }) => {
	const { index, markers, activity } = globalstate;

	const active = index >= 0 ? markers[index] : false;

	const [sw, _sw] = useState(false);

	console.log("index", index);

	useEffect(() => {
		if (index && !sw) {
			_sw(e => !e);
		}
	}, [index]);

	return (
		<div className="block activityArea">
			<nav>
				<ol>
					<li className="navi act" onClick={() => _sw(e => !e)}>
						{sw ? "<" : ">"}
					</li>
					<li className="navi">Активность</li>
					<li className="navi">Новости</li>
				</ol>
			</nav>

			<div className={"activity " + (sw ? "active" : "")}>
				<div className="b1">
					{activity.map((a, ax) => {
						return (
							<div key={ax} className="author">
								<h3>Пользователь #{a.id}</h3>
								<div className="desc">{a.content}</div>
								{a.source && a.source.length && (
									<div className="source">
										{a.source.map((s, sx) => (
											<div key={sx} className="src">
												{s.indexOf("youtube") >= 0 &&
												s.indexOf("iframe") >= 0 ? (
													<div
														dangerouslySetInnerHTML={{
															__html: s
														}}
													/>
												) : (
													<a href={s} target="_blank">
														{s}
													</a>
												)}
											</div>
										))}
									</div>
								)}
							</div>
						);
					})}
				</div>

				<div className="b2">
					<div className="author">
						<h3>Пользователь #{active.id}</h3>
						<div className="desc">{active && active.content}</div>
						{active.source && active.source.length && (
							<div className="source">
								{active.source.map((s, sx) => (
									<div key={sx} className="src">
										{s.indexOf("youtube") >= 0 &&
										s.indexOf("iframe") >= 0 ? (
											<div
												dangerouslySetInnerHTML={{
													__html: s
												}}
											/>
										) : (
											<a href={s} target="_blank">
												{s}
											</a>
										)}
									</div>
								))}
							</div>
						)}
					</div>
				</div>
			</div>
		</div>
	);
};
