import "core-js";

if (typeof window !== "undefined") {
	const ua = window.navigator.userAgent;
	if (
		ua.indexOf("MSIE") > 0 ||
		ua.indexOf("Edge") > 0 ||
		ua.indexOf("Trident") > 0
	) {
		require("promise-polyfill/src/polyfill");
		require("abortcontroller-polyfill/dist/polyfill-patch-fetch");
	}
} else {
	require("promise-polyfill");
	require("abortcontroller-polyfill");
}

import React from "react";
import App from "next/app";
import api from "api";
import RootContext, { RootProvider } from "Context";
import { memoize } from "Library";
import Head from "next/head";

const siteMeta = {
	title: "Коронавирус в Украине",
	description: "",
	icon: "coronavirus2.png",
	url:
		(typeof window !== "undefined" && window.location.href) ||
		"https://cov19.online/"
};

export default class MyApp extends App {
	static async getInitialProps({ Component, ctx }) {
		let pageProps = {};
		let presets = {};

		const isProduction = process.env.NODE_ENV === "production";

		if (Component.getInitialProps) {
			pageProps = await Component.getInitialProps(ctx);
		}

		if (ctx.req && ctx.req.url) {
			presets = await api({
				action: "front",
				method: "GET",
				params: "source=settings"
			});
		}

		return {
			loaded: true,
			mode:
				ctx.req && !ctx.req.url.includes("/admin") ? "front" : "admin",
			isProduction,
			headers: ctx.req ? ctx.req.headers : {},
			pageProps,
			presets
		};
	}

	AddToHead = memoize(() => {
		return (
			<Head>
				<link rel="shortcut icon" href={siteMeta.icon} />

				<meta name="msapplication-TileColor" content="#ffffff" />

				<meta
					name="msapplication-config"
					content="/static/favicon/browserconfig.xml"
				/>

				<meta name="theme-color" content="#ffffff" />

				<title>{siteMeta.title}</title>
			</Head>
		);
	});

	render() {
		const { Component, pageProps, router, headers, presets } = this.props;

		return (
			<RootProvider
				{...presets}
				headers={headers}
				siteMeta={siteMeta}
				router={router}>
				<RootContext.Consumer>
					{context => (
						<>
							<this.AddToHead />
							<Component {...pageProps} context={context} />
						</>
					)}
				</RootContext.Consumer>
			</RootProvider>
		);
	}
}
