import React, { useState, useCallback, useEffect } from "react";
import { Map, GoogleApiWrapper, InfoWindow, Marker } from "google-maps-react";
import markerImg from "m.png";

const MapContainer = props => {
	const [state, _state] = useState({
		activeMarker: null,
		showingInfoWindow: false
	});

	const markerSets = {
		icon: {
			url: markerImg,
			scaledSize: new google.maps.Size(defsize, defsize)
		}
	};

	const onMarkerClick = (activeMarker, index) => {
		activeMarker.animation = null;
		console.log("activeMarker", activeMarker);
		_state({
			index,
			activeMarker: Object.assign(activeMarker, {
				animation: google.maps.Animation.BOUNCE
			}),
			showingInfoWindow: true
		});
	};

	const onMapClicked = props => {
		if (state.showingInfoWindow) {
			_state(e => ({
				...e,
				showingInfoWindow: false,
				activeMarker: null
			}));
		}
	};

	const { index, activeMarker, showingInfoWindow } = state || {};

	const displayMarkers = useCallback(() => {
		return markers.map((m, mx) => (
			<Marker
				onClick={(pp, mr) => onMarkerClick(mr, mx)}
				key={mx}
				// animation={google.maps.Animation.DROP}
				{...markerSets}
				{...m}
			/>
		));
	}, [markers]);

	return (
		<Map
			{...props}
			zoom={6.5}
			style={{
				width: "100%",
				height: "100%"
			}}
			styles={styles}
			initialCenter={{ lat: 49.037868, lng: 31.904297 }}
			onClick={onMapClicked}>
			{displayMarkers()}

			<InfoWindow marker={activeMarker} visible={showingInfoWindow}>
				{index ? (
					<div>
						<h1>{markers[index].title}</h1>
					</div>
				) : (
					<></>
				)}
			</InfoWindow>
		</Map>
	);
};

