import React, { useState } from "react";
import { Map, GoogleApiWrapper, InfoWindow, Marker } from "google-maps-react";
import markerImg from "m.png";

const styles = [
	{
		featureType: "water",
		elementType: "geometry",
		stylers: [
			{
				color: "#cccccc"
			},
			{
				lightness: 17
			}
		]
	},
	{
		featureType: "landscape",
		elementType: "geometry",
		stylers: [
			{
				color: "#f5f5f5"
			},
			{
				lightness: 20
			}
		]
	},
	{
		featureType: "road.highway",
		elementType: "geometry.fill",
		stylers: [
			{
				color: "#ffffff"
			},
			{
				lightness: 17
			}
		]
	},
	{
		featureType: "road.highway",
		elementType: "geometry.stroke",
		stylers: [
			{
				color: "#ffffff"
			},
			{
				lightness: 29
			},
			{
				weight: 0.2
			}
		]
	},
	{
		featureType: "road.arterial",
		elementType: "geometry",
		stylers: [
			{
				color: "#ffffff"
			},
			{
				lightness: 18
			}
		]
	},
	{
		featureType: "road.local",
		elementType: "geometry",
		stylers: [
			{
				color: "#ffffff"
			},
			{
				lightness: 16
			}
		]
	},
	{
		featureType: "poi",
		elementType: "geometry",
		stylers: [
			{
				color: "#f5f5f5"
			},
			{
				lightness: 21
			}
		]
	},
	{
		featureType: "poi.park",
		elementType: "geometry",
		stylers: [
			{
				color: "#dedede"
			},
			{
				lightness: 21
			}
		]
	},
	{
		elementType: "labels.text.fill",
		stylers: [
			{
				saturation: 36
			},
			{
				color: "#333333"
			},
			{
				lightness: 40
			}
		]
	},
	{
		elementType: "labels.icon",
		stylers: [
			{
				visibility: "on"
			}
		]
	},
	// {
	// 	featureType: "transit",
	// 	elementType: "geometry",
	// 	stylers: [
	// 		{
	// 			color: "#f2f2f2"
	// 		},
	// 		{
	// 			lightness: 19
	// 		}
	// 	]
	// },
	// {
	// 	featureType: "administrative",
	// 	elementType: "geometry.fill",
	// 	stylers: [
	// 		{
	// 			color: "#fefefe"
	// 		},
	// 		{
	// 			lightness: 20
	// 		}
	// 	]
	// },
	{
		featureType: "administrative",
		elementType: "geometry.stroke",
		stylers: [
			{
				color: "#bebebe"
			},
			{
				lightness: 17
			},
			{
				weight: 1.2
			}
		]
	}
];

const defsize = 25;

const MapContainer = props => {
	const [state, _state] = useState({
		selectedPlace: null,
		activeMarker: null,
		showingInfoWindow: false
	});

	const markerSets = {
		icon: {
			url: markerImg,
			scaledSize: new google.maps.Size(defsize, defsize)
		}
	};

	const markers = [
		{ id: "1", title: "Title 1", label: "2", position: { lat: 50.452476, lng: 30.514183 } },
		{ id: "2", title: "Title 2", label: "1", position: { lat: 50.454225, lng: 30.60482 } },
		{ id: "3", title: "Title 3", label: "1", position: { lat: 50.443732, lng: 30.569115 } },
		{ id: "4", title: "Title 4", label: "3", position: { lat: 50.419238, lng: 30.505943 } },
		{ id: "5", title: "Title 5", label: "1", position: { lat: 50.398233, lng: 30.415306 } },
		{ id: "6", title: "Title 6", label: "1", position: { lat: 50.59043, lng: 30.214806 } },
		{ id: "7", title: "Title 7", label: "1", position: { lat: 50.501199, lng: 30.819397 } },
		{ id: "8", title: "Title 8", label: "4", position: { lat: 50.346878, lng: 30.920838 } },
		{ id: "9", title: "Title 9", label: "1", position: { lat: 50.317072, lng: 30.929078 } },
		{ id: "10",title: "Title 10",  label: "2", position: { lat: 50.341619, lng: 30.852174 } },
		{ id: "11",title: "Title 11",  label: "1", position: { lat: 50.392426, lng: 30.964783 } },
		{ id: "12",title: "Title 12",  label: "1", position: { lat: 50.429183, lng: 30.915345 } },
		{ id: "13",title: "Title 13",  label: "2", position: { lat: 50.038923, lng: 36.434741 } },
		{ id: "14",title: "Title 14",  label: "1", position: { lat: 50.081241, lng: 36.127124 } },
		{ id: "15",title: "Title 15",  label: "1", position: { lat: 49.904669, lng: 36.313891 } },
		{ id: "16",title: "Title 16",  label: "3", position: { lat: 49.975376, lng: 36.017261 } },
		{ id: "17",title: "Title 17",  label: "1", position: { lat: 50.151689, lng: 36.566577 } },
		{ id: "18",title: "Title 18",  label: "1", position: { lat: 50.299291, lng: 36.236987 } }
	];

	const onMarkerClick = (selectedPlace, activeMarker, index) => {
		_state({
			index,
			selectedPlace,
			activeMarker,
			showingInfoWindow: true
		});
	};

	const onMapClicked = props => {
		if (state.showingInfoWindow) {
			_state(e => ({
				...e,
				showingInfoWindow: false,
				activeMarker: null
			}));
		}
	};

	return (
		<Map
			{...props}
			zoom={6.5}
			style={{
				width: "100%",
				height: "100%"
			}}
			styles={styles}
			initialCenter={{ lat: 49.037868, lng: 31.904297 }}
			onClick={onMapClicked}>
			{markers.map((m, mx) => (
				<Marker
					onClick={(pp, mr) => onMarkerClick(pp, mr, mx)}
					key={mx}
					{...markerSets}
					{...m}
				/>
			))}

			<InfoWindow
				marker={state.activeMarker}
				visible={state.showingInfoWindow}>
				<div>
					<h1>ACTIVE</h1>
				</div>
			</InfoWindow>
		</Map>
	);
};

export default GoogleApiWrapper({
	apiKey: "AIzaSyA4zKpi3hRchkPewoFrU0h_V4a2ykGrohk"
})(MapContainer);
