import { useEffect, useState, useCallback } from "react";
import Pin from "pin.svg";

export default ({ globalstate, updateGlobal }) => {
	const { index, markers, news, activity } = globalstate;

	const active = index >= 0 ? markers[index] : false;

	const comments = activity[index] || false;

	const [nav, _nav] = useState("acts");

	const navigation = useCallback(key => {
		_nav(key);
	}, []);

	useEffect(() => {
		if (index >= 0) {
			navigation("local");
		}
	}, [index]);

	const SingleItem = useCallback(({ nav, a, ax, news = false }) => {
		return a ? (
			<div className="author">
				<h3
					className={"atitle" + (nav === "acts" ? " clickable" : "")}
					onClick={() => {
						if (nav === "acts") {
							updateGlobal("index", ax);
							navigation("local");
						}
					}}>
					{news ? a.title : `Пользователь #${a.id}`}
				</h3>

				<div className="desc">{a.content}</div>

				{a.source && a.source.length && (
					<div className="source">
						{a.source.map((s, sx) => (
							<RenderSource key={sx} s={s} />
						))}
					</div>
				)}

				{nav === "acts" && (
					<button
						onClick={() => {
							updateGlobal("index", ax);
							navigation("local");
						}}>
						Открыть
					</button>
				)}
			</div>
		) : (
			"Нажмите не метку"
		);
	}, []);

	const RenderSource = useCallback(({ s }) => {
		return (
			<div className="src">
				{s.indexOf("youtube") >= 0 && s.indexOf("iframe") >= 0 ? (
					<div
						dangerouslySetInnerHTML={{
							__html: s
						}}
					/>
				) : (
					<a href={s} target="_blank">
						{s}
					</a>
				)}
			</div>
		);
	}, []);

	return (
		<div className={"block activityArea " + nav}>
			<nav>
				<ol>
					{[
						{
							id: "local",
							title: <Pin />
						},
						{ id: "acts", title: "Активность" },
						{ id: "news", title: "Новости" }
					].map((m, mx) => {
						return (
							<li
								key={mx}
								className={
									"navi " + (m.id === nav ? "active" : "")
								}
								onClick={() => {
									navigation(m.id);
								}}>
								{m.title}
							</li>
						);
					})}
				</ol>
			</nav>

			<div className={"activity"}>
				<div className="bb b1">
					{markers.map((a, ax) => {
						return <SingleItem nav={nav} key={ax} a={a} ax={ax} />;
					})}
				</div>

				<div className="bb b2">
					<SingleItem nav={nav} a={active} ax={0} />
					{active && (
						<div className="comments">
							<h3>Коментарии ({comments.length || 0})</h3>
							{comments
								? comments.map((c, cx) => {
										return (
											<div className="cm author" key={cx}>
												<div className="chead">
													<b>{c.user}</b>
													{c.date ? (
														<span>
															{" - " + c.date}
														</span>
													) : (
														""
													)}
												</div>
												<div className="cbody">
													{c.content}
												</div>
											</div>
										);
								  })
								: "Пока нет коментариев, напишите первым"}
						</div>
					)}
				</div>

				<div className="bb b3">
					{news.map((a, ax) => {
						return (
							<SingleItem
								nav={nav}
								key={ax}
								news={true}
								a={a}
								ax={ax}
							/>
						);
					})}
				</div>
			</div>
		</div>
	);
};
