import { useReducer } from "react";
import Head from "next/head";
import GoogleMaps from "GoogleMaps";
import Subscribe from "Subscribe";
import MakeMark from "MakeMark";
import ActivityArea from "ActivityArea";

import "global.scss";

export const reducer = (state, a) => {
	// auto state update via KEYS
	a &&
		Object.keys(a).map(k => {
			state[k] = a[k];
			return false;
		});
	return { ...state };
};

const Home = () => {
	const [globalstate, _globalstate] = useReducer(reducer, {
		markers: [
			{
				id: "1",
				title: "Title 1",
				label: { text: "2", color: "white" },
				position: { lat: 50.452476, lng: 30.514183 }
			},
			{
				id: "2",
				title: "Title 2",
				label: { text: "1", color: "white" },
				position: { lat: 50.454225, lng: 30.60482 }
			},
			{
				id: "3",
				title: "Title 3",
				label: { text: "1", color: "white" },
				position: { lat: 50.443732, lng: 30.569115 }
			},
			{
				id: "4",
				title: "Title 4",
				label: { text: "3", color: "white" },
				position: { lat: 50.419238, lng: 30.505943 }
			},
			{
				id: "5",
				title: "Title 5",
				label: { text: "1", color: "white" },
				position: { lat: 50.398233, lng: 30.415306 }
			},
			{
				id: "6",
				title: "Title 6",
				label: { text: "1", color: "white" },
				position: { lat: 50.59043, lng: 30.214806 }
			},
			{
				id: "7",
