import React, { useState, useCallback } from "react";
import phone from "phone.png";
import email from "email.png";

const via = {
	phone,
	email
};

export default () => {
	const [state, _state] = useState({
		via: "phone",
		num: "",
		phone: "",
		email: ""
	});

	const updateState = useCallback(e => {
		const {
			target: { value }
		} = e;
		const name = e.target.getAttribute("name");

		_state(e => {
			return {
				...e,
				[name]: value
			};
		});
	}, []);

	const setVia = via => {
		_state(e => ({ ...e, via }));
	};

	return (
		<div className="block subscribe">
			<h2>
				Оставьте свой номер телефона или имейл и мы вас оповестим если
				есть рядом случай зараженния.
			</h2>

			<div className="sel">
				{["phone", "email"].map((w, x) => {
					return (
						<img
							className={w === state.via ? "selected" : ""}
							src={via[w]}
							onClick={() => setVia(w)}
						/>
					);
				})}

				<div className="inpf">
					{
						{
							phone: <>111</>,
							email: <>222</>
						}[state.via || ""]
					}
				</div>
			</div>
		</div>
	);
};
