import React, { memo, useState, useCallback } from "react";

export default memo(() => {
	const [visible, _visible] = useState(false);

	const [email, _email] = useState("");
	const [city, _city] = useState("");
	const [street, _street] = useState("");
	const [content, _content] = useState("");

	const togglePopup = useCallback(() => _visible(e => !e), []);

	const clickOut = e => {
		if (!document.getElementById("form").contains(e.target)) {
			togglePopup();
		}
	};

	return (
		<>
			<div className="block makeMark">
				<button onClick={togglePopup}>Сообщить о заражении</button>
			</div>

			{visible && (
				<div id="popup" className="popup" onClick={clickOut}>
					<div id="form" className="form">
						<h2>
							Заполните форму ниже и нажмите кнопку отправить.
						</h2>

						<ul>
							<li>Не указывайте адрес квартиры или дома</li>
							<li>Не указывайте инициалы</li>
							<li>Не указывайте инициалы</li>
						</ul>

						<input
							type="email"
							placeholder="Введите вашу электронную почту"
							onChange={({ target: { value } }) => _email(value)}
							value={email}
						/>

						<input
							type="text"
							placeholder="Город заражения"
							onChange={({ target: { value } }) => _city(value)}
							value={city}
						/>

						<input
							type="text"
							placeholder="Улица заражения"
							onChange={({ target: { value } }) => _street(value)}
							value={street}
						/>

						<textarea
							placeholder="Опишите случай"
							onChange={({ target: { value } }) =>
								_content(value)
							}
							value={content}
						/>

						<input
							type="number"
							placeholder="Количество заразившихся"
						/>

						<input
							type="text"
							placeholder="ссылка на ресурс (Youtube, новостной сайт)"
							onChange={({ target: { value } }) => _street(value)}
							value={street}
						/>

						<button>Отправить</button>
					</div>
				</div>
			)}
		</>
	);
});
