import { useEffect, useState } from "react";

const SingleItem = ({ a, ax, title }) => {
	return (
		<div key={ax} className="author">
			<h3
				style={{ textDecoration: "underline" }}
				onClick={() => {
					updateGlobal("index", ax);
				}}>
				Пользователь #{a.id}
			</h3>
			<div className="desc">{a.content}</div>
			{a.source && a.source.length && (
				<div className="source">
					{a.source.map((s, sx) => (
						<div key={sx} className="src">
							{s.indexOf("youtube") >= 0 &&
							s.indexOf("iframe") >= 0 ? (
								<div
									dangerouslySetInnerHTML={{
										__html: s
									}}
								/>
							) : (
								<a href={s} target="_blank">
									{s}
								</a>
							)}
						</div>
					))}
				</div>
			)}
		</div>
	);
};

export default ({ globalstate, updateGlobal }) => {
	const { index, markers } = globalstate;

	const active = index >= 0 ? markers[index] : false;

	const [sw, _sw] = useState(false);
	const [nav, _nav] = useState("acts");

	useEffect(() => {
		if (index >= 0 && !sw) {
			_sw(e => !e);
		}
	}, [index]);

	return (
		<div className={"block activityArea " + nav}>
			<nav>
				<ol>
					<li className="navi act" onClick={() => _sw(e => !e)}>
						{sw ? "<" : ">"}
					</li>
					{[
						{ id: "acts", title: "Активность" },
						{ id: "news", title: "Новости" }
					].map((m, mx) => {
						return (
							<li
								key={mx}
								className={
									"navi " + (m.id === nav ? "active" : "")
								}
								onClick={() => {
									_nav(m.id);
									m.id === "acts" && _sw(false);
								}}>
								{m.title}
							</li>
						);
					})}
				</ol>
			</nav>

			<div className={"activity " + (sw ? "active" : "")}>
				<div className="bb b1">
					{markers.map((a, ax) => {
						return <SingleItem a={a} ax={ax} />;
					})}
				</div>

				<div className="bb b2">
					<div className="author">
						{active ? (
							<>
								<h3>Пользователь #{active.id}</h3>
								<div className="desc">
									{active && active.content}
								</div>
								{active.source && active.source.length && (
									<div className="source">
										{active.source.map((s, sx) => (
											<div key={sx} className="src">
												{s.indexOf("youtube") >= 0 &&
												s.indexOf("iframe") >= 0 ? (
													<div
														dangerouslySetInnerHTML={{
															__html: s
														}}
													/>
												) : (
													<a href={s} target="_blank">
														{s}
													</a>
												)}
											</div>
										))}
									</div>
								)}
							</>
						) : (
							"нажмите на маркер"
						)}
					</div>
				</div>
				<div className="bb b3">...</div>
			</div>
		</div>
	);
};
